                // Função para formatar datas de YYYY-MM-DD para DD/MM/YYYY
                function formatarData(data) {
                    if (!data || data === '--') return '--';
                    const [ano, mes, dia] = data.split('-');
                    return `${dia}/${mes}/${ano}`;
                }


                document.addEventListener("DOMContentLoaded", function() {
                console.log("DOM completamente carregado e analisado");

                const turmaDropdown = document.getElementById('cod-turma-dropdown');
                const turmaDiscDropdown = document.getElementById('id-turma-disc-dropdown');
                const disciplinaDropdown = document.getElementById('disciplina-visualizacao');
                const eduInfoContainer = document.getElementById('edu_info_container');
                const turmaPesquisasValue = document.getElementById('turma-pesquisas-value');
                const turmaRespondidasValue = document.getElementById('turma-respondidas-value');
                const turmaPercentualRespondidasValue = document.getElementById('turma-percentual-respondidas-value');
                const unidade = document.getElementById('unidade-value');
                const disciplinaInfo = document.getElementById('disciplina_info');
                const disciplinaVisualizacao = document.getElementById('disciplina-visualizacao');
                const disciplinaPesquisasValue = document.getElementById('disciplina-pesquisas-value');
                const disciplinaRespondidasValue = document.getElementById('disciplina-respondidas-value');
                const disciplinaPercentualRespondidasValue = document.getElementById('disciplina-percentual-respondidas-value');
                const dtFinalValue = document.getElementById('turma-dt-final-value');
                const dtInicialValue = document.getElementById('turma-dt-inicial-value'); 
                const modalidadeValue = document.getElementById('turma-modalidade-value'); 
                const disciplinaDtFinalValue = document.getElementById('disciplina-dt-final-value');
                const disciplinaDtInicialValue = document.getElementById('disciplina-dt-inicial-value');
                const respondentesProgressBar = document.getElementById('respondentes-progress-bar'); 

                const turmaData = ;


                console.log("Dados da turma carregados:", turmaData);

                turmaDropdown.addEventListener('change', function() {
                    const selectedTurma = this.value;
                    console.log("Turma selecionada:", selectedTurma);

                    turmaDiscDropdown.innerHTML = 'Selecione um ID de Turma';
                    turmaDiscDropdown.classList.add('select-disabled');

                    if (selectedTurma && turmaData[selectedTurma]) {
                        turmaDiscDropdown.classList.remove('select-disabled');
                        Object.keys(turmaData[selectedTurma]).forEach(idTurmaDisc => {
                            const option = document.createElement('option');
                            option.value = idTurmaDisc;
                            option.text = idTurmaDisc;
                            turmaDiscDropdown.appendChild(option);
                        });
                    }
                });

                // Event listener for selecting ID de Turma        
                turmaDiscDropdown.addEventListener('change', function() {
                    const selectedTurma = turmaDropdown.value;
                    const selectedIdTurmaDisc = this.value;
                    console.log("ID de Turma selecionado:", selectedIdTurmaDisc);

                    if (selectedTurma && selectedIdTurmaDisc && turmaData[selectedTurma][selectedIdTurmaDisc]) {
                        const data = turmaData[selectedTurma][selectedIdTurmaDisc];
                        console.log("Dados da Turma Disciplina selecionada:", data);

                        // Display dt_final, dt_inicial, modalidade, and periodo_letivo for Turma
                        dtFinalValue.innerText = data.dt_final || '--';
                        dtInicialValue.innerText = data.dt_inicial || '--'; // Atualização para data inicial
                        modalidadeValue.innerText = data.modalidade || 'N/A'; // Atualização para modalidade
                        unidade.innerText = data.unidade;
                        turmaPesquisasValue.innerText = data.pesquisas;
                        turmaRespondidasValue.innerText = data.respondidas;
                        turmaPercentualRespondidasValue.innerText = data.percentual_respondidas;

                        eduInfoContainer.classList.remove('d-none');


                        // Clear and hide disciplina info
                        disciplinaPesquisasValue.innerText = '0';
                        disciplinaRespondidasValue.innerText = '0';
                        disciplinaPercentualRespondidasValue.innerText = '0%';
                        disciplinaDtFinalValue.innerText = '--';
                        disciplinaInfo.classList.add('d-none');

                        disciplinaVisualizacao.value = Object.keys(data.disciplinas)[0];
                        console.log("Disciplina selecionada inicialmente:", disciplinaVisualizacao.value);

                        // Atualizar barra de Respondentes
                        calcularRespondentesPesquisa(selectedTurma, selectedIdTurmaDisc);

                    } else {
                        resetarIndicadoresRespondentes();

                    }
                });




                // Função para calcular Respondentes da Pesquisa
                function calcularRespondentesPesquisa(selectedTurma, selectedIdTurmaDisc) {
                    const disciplinas = turmaData[selectedTurma][selectedIdTurmaDisc].disciplinas;
                    let somaProdutos = 0;
                    let somaPesquisas = 0;

                    Object.values(disciplinas).forEach(disciplina => {
                        const percentualRespondidas = parseFloat(disciplina.percentual_respondidas) / 100; // Converter para decimal
                        const quantidadePesquisas = parseFloat(disciplina.pesquisas);

                        const produto = percentualRespondidas * quantidadePesquisas;
                        somaProdutos += produto;
                        somaPesquisas += quantidadePesquisas;
                    });

                    // Evitar divisão por zero
                    const respondentesPesquisa = somaPesquisas > 0 ? (somaProdutos / somaPesquisas) * 100 : 0;
                    console.log("Respondentes da Pesquisa Calculado:", respondentesPesquisa.toFixed(2) + "%");

                    // Atualizar o DOM
                    respondentesProgressBar.style.width = respondentesPesquisa + '%';
                    respondentesProgressBar.innerText = respondentesPesquisa.toFixed(2) + '%';

                    // Atualizar cores conforme o valor (opcional)

                }


                // Função para resetar os indicadores de Respondentes da Pesquisa
                function resetarIndicadoresRespondentes() {
                    respondentesProgressBar.style.width = '0%';
                    respondentesProgressBar.innerText = '0%';
                }
















                // Trigger change event to show all records initially
                console.log("Disparando o evento change inicialmente");
                turmaDropdown.dispatchEvent(new Event('change'));
            });
